  launches(after: $after) {
    cursor
    hasMore
    launches {
      id
      isBooked
      rocket {
        id
        name
      }
      mission {
        name
        missionPatch
      }
    }
  }
}

query LaunchDetails($launchId: ID!) {
  launch(id: $launchId) {
    id
    site
    isBooked
    rocket {
      id
      name
      type
    }
    mission {
      name
      missionPatch
    }
  }
}

